document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll('[data-trigger="modal"]');e.forEach(function(e){e.addEventListener("click",function(t){t.preventDefault();for(var n=null,i=e.nextElementSibling;i;){if("modal-target"===i.getAttribute("data-trigger")){n=i;break}i=i.nextElementSibling}if(!n)for(var r=e.previousElementSibling;r;){if("modal-target"===r.getAttribute("data-trigger")){n=r;break}r=r.previousElementSibling}n&&n.click()})})});